import React, { useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import ShowImage from './ShowImage';
import moment from 'moment';
import { addItem, updateItem, removeItem } from './cartHelpers';

const Card = ({
  product,
  showViewProductButton = true,
  showAddToCartButton = true,
  cartUpdate = false,
  showRemoveProductButton = false,
  setRun = f => f,
  run = undefined
  // changeCartSize
}) => {
  const [redirect, setRedirect] = useState(false);
  const [count, setCount] = useState(product.count);

  const showViewButton = showViewProductButton => {
    return (
      showViewProductButton && (
        <Link to={`/product/${product._id}`} className="mr-2">
          <button className="btn btn-outline-primary mt-2 mb-2 card-btn-1">View Product</button>
        </Link>
      )
    );
  };
  const addToCart = () => {
    // console.log('added');
    addItem(product, setRedirect(true));
  };

  const shouldRedirect = redirect => {
    if (redirect) {
      return <Redirect to="/cart" />;
    }
  };

  const showAddToCartBtn = showAddToCartButton => {
    return (
      showAddToCartButton && (
        <button onClick={addToCart} className="btn btn-outline-warning mt-2 mb-2 card-btn-1  ">
          Add to cart
        </button>
      )
    );
  };

  const showStock = quantity => {
    return quantity > 0 ? (
      <span className="badge badge-primary badge-pill">In Stock </span>
    ) : (
      <span className="badge badge-primary badge-pill">Out of Stock </span>
    );
  };

  const handleChange = productId => event => {
    setRun(!run); // run useEffect in parent Cart
    setCount(event.target.value < 1 ? 1 : event.target.value);
    if (event.target.value >= 1) {
      updateItem(productId, event.target.value);
    }
  };

  const showCartUpdateOptions = cartUpdate => {
    return (
      cartUpdate && (
        <div>
          <div className="input-group mb-3">
            <div className="input-group-prepend">
              <span className="input-group-text">Adjust Quantity</span>
            </div>
            <input type="number" className="form-control" value={count} onChange={handleChange(product._id)} />
          </div>
        </div>
      )
    );
  };
  const showRemoveButton = showRemoveProductButton => {
    return (
      showRemoveProductButton && (
        <button
          onClick={() => {
            removeItem(product._id);
            setRun(!run); // run useEffect in parent Cart
          }}
          className="btn btn-outline-danger mt-2 mb-2"
        >
          Remove Product
        </button>
      )
    );
  };
  return (
    // <div className="card ">
    //   <div className="card-header card-header-1 ">{product.name}</div>
    //   <div className="card-body">
    //     {shouldRedirect(redirect)}
    //     <ShowImage item={product} url="product" />
    //     <p className="card-p  mt-2">{product.description.substring(0, 100)} </p>
    //     <p className="card-p black-10">$ {product.price}</p>
    //     <p className="black-9">Category: {product.category && product.category.name}</p>
    //     <p className="black-8">Added on {moment(product.createdAt).fromNow()}</p>
    //     {showStock(product.quantity)}
    //     <br />

    //     {showViewButton(showViewProductButton)}

    //     {showAddToCartBtn(showAddToCartButton)}

    //     {showRemoveButton(showRemoveProductButton)}

    //     {showCartUpdateOptions(cartUpdate)}
    //   </div>
    // </div>

        <div className="card">
          <div className="card-header card-header-1 ">{product.name}</div>
          <div className="card-body pt-0 px-0">
           {shouldRedirect(redirect)}
           <ShowImage item={product} url="product" />
            <hr className="mt-2 mx-3" />
            <div className="d-flex flex-row justify-content-between mb-0 px-3"> <small className="text-muted mt-1"></small>
              <h6>{product.description.substring(0, 100)}</h6>
            </div>
            <hr className="mt-2 mx-3" />
            <div className="d-flex flex-row justify-content-between px-3 pb-4">
              <div className="d-flex flex-column"><span className="text-muted">Fuel Efficiency</span><small className="text-muted">L/100KM*</small></div>
              <div className="d-flex flex-column">
                <h5 className="mb-0">8.5/7.1</h5><small className="text-muted text-right">(city/Hwy)</small>
              </div>
            </div>
            <div className="d-flex flex-row justify-content-between p-3 mid">
              <div className="d-flex flex-column"><small className="text-muted mb-1">ENGINE</small>
                <div className="d-flex flex-row"><img src="https://imgur.com/iPtsG7I.png" width="35px" height="25px" />
                  <div className="d-flex flex-column ml-1"><small className="ghj">1.4L MultiAir</small><small className="ghj">16V I-4 Turbo</small></div>
                </div>
              </div>
              <div className="d-flex flex-column"><small className="text-muted mb-2">HORSEPOWER</small>
                <div className="d-flex flex-row"><img src="https://imgur.com/J11mEBq.png" />
                  <h6 className="ml-1">135 hp*</h6>
                </div>
              </div>
            </div> <small className="text-muted key pl-3">Standard key Features</small>
              <div className="mx-3 mt-3 mb-2"><button type="button" className="btn  btn-success  btn-block">View Details</button></div>
              <div className="mx-3 mt-3 mb-2"><button type="button" className="btn btn-success btn-block">Add to Cart</button> </div> 

          </div>
        </div>

  );
};

export default Card;
