import React, { useState, useEffect } from "react";
import { getCategories, list } from "./apiCore";
import Layout from "./Layout";
import Card from "./Card";
import {useDispatch}  from 'react-redux';
import '../core/coreStyle/searchComponent.css';
import {setSearchResult} from '../redux/action'


const Search = () => {
    const [data, setData] = useState({
        categories: [],
        category: "",
        search: "",
        results: [],
        searched: false
    });

    const dispatch = useDispatch();

    const { categories, category, search, results, searched } = data;

    useEffect(() => {

        const loadCategories = async () => {
            try{
                const data = await getCategories();
                setData({ ...data, categories: data });
            }
            catch(error){
                throw console.error();
            }
        };
        loadCategories();
    }, []);

    const searchData = () => {
        // console.log(search, category);
        if (search) {
            list({ search: search || undefined, category: category }).then(
                response => {
                    if (response.error) {
                        console.log(response.error);
                    } else {
                        dispatch(setSearchResult(response));
                        setData({ ...data, results: response, searched: true });
                    }
                }
            );
        }
    };

    const searchSubmit = e => {
        e.preventDefault();
        searchData();
    };

    const handleChange = name => event => {
        setData({ ...data, [name]: event.target.value, searched: false });
    };

    const searchMessage = (searched, results) => {
        if (searched && results.length > 0) {
            return `Found ${results.length} products`;
        }
        if (searched && results.length < 1) {
            return `No products found`;
        }
    };

    const searchedProducts = (results = []) => {
        return (
            <div>
                <h2 className="mt-4 mb-4">
                    {searchMessage(searched, results)}
                </h2>

                <div className="row">
                    {results.map((product, i) => (
                        <div className="col-4 mb-3">
                            <Card key={i} product={product} />
                        </div>
                    ))}
                </div>
            </div>
        );
    };

    const searchForm = () => (
        <form onSubmit={searchSubmit} className='search-form'> 
            <span className="input-group-text">
                <div className="input-group input-group-lg">
                    <div className="input-group-prepend">
                        <select
                            className="btn mr-2"
                            onChange={handleChange("category")}
                        >
                            <option value="All">All</option>
                            {categories.map((c, i) => (
                                <option key={i} value={c._id}>
                                    {c.name}
                                </option>
                            ))}
                        </select>
                    </div>

                    <input
                        type="search"
                        className="form-control"
                        onChange={handleChange("search")}
                        placeholder="Search by name"
                    />
                </div>
                <div
                    className="btn input-group-append"
                    style={{ border: "none" }}
                >
                    <button className="input-group-text btn search-button" type="submit">Search</button>
                </div>
            </span>
        </form>
    );

    return (

            <div className="row">
                <div className="container mb-3">{searchForm()}</div>  
                <div className="container-fluid mb-3">
                    {searchedProducts(results)}
                </div>          
            </div>
    );
};

export default Search;
